const initElements=()=>{const a=document.getElementById("sample-audio"),b=document.getElementById("pause-btn"),c=document.getElementById("play-btn");return{audio:a,pause:b,play:c}},customPlay=()=>{const{audio:a,pause:b,play:c}=initElements();console.log("play pressed"),a.paused?(a.play(),b.style.visibility="visible",c.style.visibility="hidden"):(a.pause(),c.style.visibility="visible",b.style.visibility="hidden")},stopPlay=()=>{const{audio:a,pause:b,play:c}=initElements();a.pause(),c.style.visibility="visible",b.style.visibility="hidden"};